mapper.select:
SELECT job_title as jobTitle, job_description as jobDescription, company_name as companyName, city as city, `status` as status, submission_time as submissionTime, approval_time as approvalTime, rejection_reason as rejectionReason, recruit_num as recruitNum, recruited_num as recruitedNum, byword as byword, job_duties as jobDuties, min_salary as minSalary, max_salary as maxSalary, `name` as departmentName FROM recruitment_info, enterprise, department WHERE recruitment_info.enterprise_id = enterprise.enterprise_id AND recruitment_info.department_id = department.department_id
AND department.name=#{departmentName}
AND company_name=#{companyName}
AND job_title=#{jobTitle}
