mapper.select:
SELECT r.recruitment_id as recruitmentId, job_title as jobTitle, company_name as companyName, city as city, min_salary as minSalary, max_salary as maxSalary, byword as byword, job_description as jobDescription, job_duties as jobDuties, CASE WHEN r.recruitment_id = m.recruitment_id AND m.student_id=#{userId} THEN 1 ELSE 0 END AS status FROM recruitment_info AS r ,marked_recruitment_info AS m WHERE r.recruitment_id = m.recruitment_id AND m.student_id=#{userId} AND recruit_num > recruited_num
AND (r.job_title LIKE CONCAT('%', #{queryInfo}, '%') OR r.company_name LIKE CONCAT('%', #{queryInfo}, '%'))
AND r.min_salary >= #{minSalary}
AND r.max_salary <= #{maxSalary}
