mapper.select:
SELECT r.recruitment_id as recruitmentId, job_title as jobTitle, company_name as companyName, city as city, min_salary as minSalary, max_salary as maxSalary, byword as byword, CASE WHEN r.recruitment_id = m.recruitment_id AND m.student_id=#{userId} THEN 1 ELSE 0 END AS state FROM recruitment_info AS r LEFT JOIN marked_recruitment_info AS m ON r.recruitment_id = m.recruitment_id WHERE 1=1
AND (r.job_title LIKE CONCAT('%', #{queryInfo}, '%') OR r.company_name LIKE CONCAT('%', #{queryInfo}, '%'))
AND r.min_salary >= #{minSalary}
AND r.max_salary <= #{maxSalary}
